mapper.select:
	select id, company_name, company_logo, link_person, link_phone, provice_code, city_code, 
  county_code, address, coordinate,company_type,register_time from t_company_info
  where 1=1
    
      	and company_name  like '%${params.searchText}%' 
  
    
      	and provice_code=#{params.proviceCode}
  
    
      	and city_code=#{params.cityCode}
  
    
      	and county_code=#{params.countyCode}
  
    
      	and company_type=#{params.companyType}
