mapper.select:
select 

from t_reg_verification
where 1=1 

    and id = #{id,jdbcType=INTEGER}


   and saleman_id =#{salemanId,jdbcType=INTEGER}


   and state =#{state,jdbcType=INTEGER}

mapper.update:
update t_reg_verification
set saleman_id = #{salemanId,jdbcType=INTEGER},
  invitation_code = #{invitationCode,jdbcType=VARCHAR},
  state = #{state,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, saleman_id, invitation_code, state, creat_time, update_time from t_reg_verification t where 1=1
and state=#{params.state}
order by id
